import {React,useState} from "react"
import Cart from "../cart/Cart";


const Header = () => {
  const [open, setOpen] = useState(false);
  const handleModal = () => <Cart opened = {handleOpen} closed = {handleClose}/>
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  return (
    <nav className="navbar navbar-light bg-light justify-content-between">
      <a className="navbar-brand">ReactMeal</a>
      <form className="form-inline">
        <button className="btn btn-outline-success my-2 my-sm-0" type="submit" onClick={ () => { handleModal() } }><span>cart</span><span>0</span></button>
      </form>
    </nav>
  );
};
export default Header;
