import {React,useState} from "react"
import Cart from "../cart/Cart";


const Header = (props) => {
  const [open, setOpen] = useState(false);
  const [modal, setModal] = useState(false);
  const openModal = () => setModal(true);
  const closeModal = () => setModal(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const handleShow = () =>{
    if (props.cart.length === 0) {
      return props.cart.length;
    } else {
      return 0;
    }
  }
  console.log(props.cart)
  return (
    <>
    <nav className="navbar navbar-light bg-light justify-content-between">
      <a className="navbar-brand">ReactMeal</a>
      <button className="btn btn-outline-success my-2 my-sm-0" onClick={openModal}>cart<span>{handleShow}</span></button>
      {modal && <Cart opened = {handleOpen} closed = {handleClose} closedModal = {closeModal}/>}
    </nav>
    </>
  );
};
export default Header;
