import React from "react";
const Item = ({item}) => {
  return(
    <li style={style}>
      <div>
        <h3><b>{item.name}</b></h3>
        <p>{props.item.description}</p>
        <h5>{props.item.price}</h5>
      </div>
      <div>
        <form>
          Amount <input placeholder="1"/><br/>
          <button>Add</button>
        </form>
      </div>
    </li>
  )
}
const style = {
  display: "flex",
  flexDirection: "row",
  justifyContent: "space-between",
  alignItem: "center"
}
export default Item;
