import React from 'react';
import Item from "./item";
import Data from "../../data"
const Menu = (props) => {
  console.log(props.cart, 'cart');
  const handleCart = (data) => {
    if (props.cart.length !== 0){
      props.cart.map((item) => {
        if (item.id == data.id){
          let array = props.cart
          let new_array = array.filter(i => i.id !== data.id)
          console.log(new_array, 'new array');
          new_array.push(data)
          console.log(new_array, 'after push');

          return props.setCart(new_array);
        } else {
          console.log('in else')
          return props.setCart([...new Map(array.map(item =>
            [item[key], item])).values()])
          return props.setCart([...props.cart, data])
        }
      })
    } else {
      return props.setCart(data)
    }
  }

  return(
    <div>
      <ul style={{listStyleType: "none"}}>
        {Data.map((item, index) => <Item key = {index} item = {item} setCart = {handleCart} cart = {props.cart}/>)}
      </ul>
    </div>
  )
}


export default Menu;
