import React, {useState, useEffect } from 'react';
import MyContext from '../../MyContext';
const Item = (props) => {
  const [count, setCount] = useState(1);
  const handleInputChange = (event) => {
    setCount(event.target.value);
  }

  const handleSubmit = () => {
    const cartItem = {data: props.item, count: count};
    props.setCartData((prevState) => (
      [...prevState, cartItem]
    ))
  }
  console.log("data::",props.cartData )
  return(
    <div>
      <li style={style}>
        <div>
          <h3><b>{props.item.name}</b></h3>
          <p>{props.item.description}</p>
          <h5>{props.item.price}</h5>
        </div>
        <div>
            Amount <input type = "number" step = "1" max = "5" min = "1" value={count} onChange={handleInputChange} placeholder = {count}/><br/>
            <button onClick={handleSubmit}>Add</button>
        </div>
      </li>
    </div>
  )
}
const style = {
  display: "flex",
  flexDirection: "row",
  justifyContent: "space-between",
  alignItem: "center"
}
export default Item;
