  const MyContext = createContext();
  const { value, setValue } = useContext(MyContext);

  return(
    <div>
      <ul style={{listStyleType: "none"}}>
        {context.data.map((item, index) => <Item key = {index} item = {item} setValue = {setValue} />)}
      </ul>
    </div>
  )
}


export default Menu;
