import {React,useState} from "react"
import Cart from "../cart/Cart";
import "./header.css"


const Header = (props) => {
  const [open, setOpen] = useState(false);
  const [modal, setModal] = useState(false);
  const openModal = () => setModal(true);
  const closeModal = () => setModal(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleCart = (data) => {
    let array = props.cart
    let new_array = array.filter(i => i.id !== data.id);
    if (data.count > 0){
      new_array.push(data)
    }
    props.setCart(new_array);
  }


  return (
    <nav className="navbar justify-content-between header">
      <div>
        <a className="navbar-brand"><span style={{color: "white"}}>ReactMeal</span></a>
      </div>
      <button onClick={openModal} style={{backgroundColor: "#4d1601", padding: "10px 20px"}}><span style={{color: "white"}}>cart</span><span style={{color: "white"}}>{props.cart.length}</span></button>
      {modal && <Cart data = {props.cart}  setCart = {handleCart} opened = {handleOpen} closed = {handleClose} closedModal = {closeModal}/>}
    </nav>
  );
};

export default Header;
