import {React,useState} from "react"
import Cart from "../cart/Cart";
import "./header.css"


const Header = (props) => {
  const [open, setOpen] = useState(false);
  const [modal, setModal] = useState(false);
  const openModal = () => setModal(true);
  const closeModal = () => setModal(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleCart = (data) => {
    if(props.cart.find(item => item.id === data.id)){
      let array = props.cart
      let new_array = array.filter(i => i.id !== data.id);
      new_array.push(data)
      return props.setCart(new_array);
    } else {
      return props.setCart([...props.cart, data])
    }
  }


  return (
    <nav className="navbar navbar-light bg-light justify-content-between">
      <a className="navbar-brand">ReactMeal</a>
      <button className="btn btn-outline-success my-2 my-sm-0" onClick={openModal}>cart<span>{props.cart.length}</span></button>
      {modal && <Cart data = {props.cart} handleCart = {props.setCart} opened = {handleOpen} closed = {handleClose} closedModal = {closeModal}/>}
    </nav>
  );
};
export default Header;
