import { useEffect } from "react";
import Data from "../../store/data";

import { useRef } from 'react';
import classes from './newItem.module.css';

const NewItem = () => {
  useEffect(()=>{
    Data.push({id: 'm5', name: "biryani", description: "it's asian region recipe", price: 1.2, count: 1})
  })
  console.log(Data);

  const titleInputRef = useRef();
  const imageInputRef = useRef();
  const addressInputRef = useRef();
  const descriptionInputRef = useRef();

  function submitHandler(event) {
    event.preventDefault();

    const enteredName = nameInputRef.current.value;
    const enteredPrice = parseFloat(priceInputRef.current.value);
    const enteredDescription = descriptionInputRef.current.value;

    const meetupData = {
      id: Data.length,
      name: enteredName,
      price: enteredPrice,
      address: enteredAddress,
      description: enteredDescription,
    };

    props.onAddMeetup(meetupData);
  }
  return(
    <div>
      <form className={classes.form} onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor='name'>Recipe Name</label>
          <input type='text' required id='name' ref={nameInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='price'>Meetup Image</label>
          <input type='number' required id='price' ref={priceInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='description'>Description</label>
          <textarea
            id='description'
            required
            rows='5'
            ref={descriptionInputRef}
          ></textarea>
        </div>
        <div className={classes.actions}>
          <button>Add Meetup</button>
        </div>
      </form>
    </div>
  )
}
export default NewItem;
