import React, {useState } from 'react';

const Item = (props) => {
  const [count, setCount] = useState(1);
  const handleInputChange = (event) => {
    setCount(event.target.value);
  }

  const handleSubmit = () => {
    if (props.cart.length !== 0){
      props.cart.map((i) => {
        if (i.id === props.item.id){
          return {...i, count: count}
        } else{
          props.item.count = count
          props.cart(prevState =>
            [...prevState, props.item]
          )
        }
        return i;
      })
    } else {
      props.item.count = count;
      return props.setCart(props.item)
    }
  }

  return(
    <div>
      <li style={style}>
        <div>
          <h3><b>{props.item.name}</b></h3>
          <p>{props.item.description}</p>
          <h5>{props.item.price}</h5>
        </div>
        <div>
            Amount <input type = "number" step = "1" max = "5" min = "1" value={count} onChange={handleInputChange} placeholder = {count}/><br/>
            <button onClick={handleSubmit}>Add</button>
        </div>
      </li>
    </div>
  )
}
const style = {
  display: "flex",
  flexDirection: "row",
  justifyContent: "space-between",
  alignItem: "center"
}
export default Item;
